import React from 'react';
import axios from "axios";
import { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { Link } from 'react-router-dom';
import API_URL from '../../config';
import '../../App.css';

function AddNewShoe({props}) {

    const [shoe, setShoe] = useState([]);
	const { id } = useParams();
	const [newShoe, setNewShoe] = useState({});
	const [problem,setProblem] = useState(false);
	let history = useHistory();

	const getShoes = () => {
		axios.get(`${API_URL}/shoes/`) 
		  .then((response) => {
			setShoe(response.data);
			console.log(response.data)
		  })
		  .catch((e) => {
			console.error(e);
		  });
		}

	useEffect(() => {
		getShoes();


	}, []);

    const handleChange = (event) => {
		let type= event.target.type;
		let value = event.target.value;
		setNewShoe({ ...newShoe, [type]: value });
	};


	const handleSubmit = async (event) => {
		event.preventDefault();		
		const verify = window.confirm(`Are you sure you want to create this?`)
		if (verify) {
		try {
			const Shoe = await axios.post(`${API_URL}/shoes`, newShoe)

			Shoe.status === 201 && history.push('/');
		} catch (error) {
			setProblem(error)

		}
		}  else {
			return;
		}
	};
	if (!shoe) {
		<h1>loading</h1>;
	}

   		 return (
      		  <div>
				{problem && <hh>{problem}</hh>}
          	  <form>
                 <label>
                        TYPE:
                     <input type="text" name="name" />
                 </label> 
                 <label>
                    STYLING:
                    <input type="text" name="name" />
                 </label>
                 <label>
                    DETELS:
                    <input type="text" name="name" />
                 </label>
                 <label>
                    DESCRIPTION:
                    <input type="text" name="name" />
                 </label>
                 <label>
                    BRAND NAME:
                    <input type="text" name="name" />
                 </label>
                 <label>
                    BRAND URL:
                    <input type={URL} name="name" />
                 </label>
                 <label>
                    SHHOE URL:
                    <input 
					type={URL} 
					name="name" 
					value= {newShoe.brand_url}
					 />
                 </label>
                 <label>
                  PHOTO:
                    <input
					 type= {Image} 
					 name="name" 
					 value = {newShoe.photo}/>
					/>
                 </label>
                 <button className='app-button' type='submit' onclick={handleSubmit} >Submit</button>
            </form>
        </div>
    );
}

export default AddNewShoe;
