import React from 'react';
import axios from "axios";
import { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import { Link } from 'react-router-dom';
import API_URL from '../../config';
import '../../App.css';

function Details({props}) {

	const [newShoe, setNewShoe] = useState({});
    const [singleShoe, setSingleShoe] = useState([]);
    const [newObject, setNewObject] = useState({});
	const [edit, setEdit] = useState(false);
	const { id } = useParams();
	let history = useHistory();

	useEffect(() => {
		axios
			.get(`${API_URL}/shoes/${id}`)
			.then((item) => setSingleShoe(item.data))
			.catch((error) => console.log(error));
	}, []);



	const handleChange = (event) => {
		let name = event.target.name;
		let value = event.target.value;
		setNewObject({ ...newObject, [name]: value });
	};
	const handleEdit = () => {
		setEdit(!edit);
	};
	const handleSubmit = async (event) => {
		event.preventDefault();		
		try {
			const data= new FormData()
			   data.append('photo', newObject.photo)
			   data.append('type', newObject.type)
			   data.append('brand_name',newObject.brand_name)
			   data.append('brand_url', newObject.brand_url)
			   data.append('styling', newObject.styling)
			   data.append('details', newObject.details)
			   data.append('description', newObject.description)

			const newShoe = await axios.post(`${API_URL}/shoes/`, data, {'Content-Type': 'multipart/form-data',})
			newShoe.status === 201 && history.push('/');
		} catch (error) {
			console.log(error);
		}
	};
		const handleDelete = async (event) => {
		const verify = window.confirm('Are you sure you want to delete?');
		if (verify) {
			try {
				const deleteOne = await axios.delete(`${API_URL}/shoes/${id}`);
				deleteOne.status === 204 && history.push('/');
			} catch (error) {
			console.log(error);
			}
		} else {
			return;
		}
	};
	if (!singleShoe) {
		<h1>loading</h1>;
	}
   		 return (
            <>
        	{!edit ? (
                <>
                <div className='detail-shoes'>
						<h1 id='detail-shoe-item'>{singleShoe.type}</h1>
						<p className='single-detail' id='detail-shoe-item'>
							{singleShoe.description}
						</p>
						<img src={singleShoe.photo} alt="" id='detail-shoe-item'></img> 
						<section id='detail-item-addresses'>
							<p className='detail-item'>brand name: {singleShoe.brand_name}</p>
							<a
								href={`${singleShoe.brand_url}`}
								alt='website link'
								className='detail-item'
								id='detail-item-link'>
								Brand URL : {singleShoe.brand_url}
							</a>
						</section>
						<aside className='buttons-flex'>
							<button className='app-button' onClick={handleEdit}>
								Edit
							</button>
							<button className='app-button' onClick={handleDelete}>
								Delete
							</button>
						</aside>
					</div>
                </>
            ):(
                <>
             <form className="form" onSubmit={handleSubmit} onEdit={handleEdit}>
                 <label className="label-add" htmlFor='id'>
                        TYPE : 
                     <input className="input-add"
					 id='id'
                     onSubmit={handleSubmit}
					 onChange={handleChange}
					 type="text" 
					 name="type"
					 value= {newShoe.type}
					 />
                 </label> 
                 <label className="label-add" htmlFor='id'>
                    STYLING :
                    <input className="input-add"
					id='id'
                    onSubmit={handleSubmit}
					onChange={handleChange}
					type="text" 
					name="styling" 
					value= {newShoe.styling}
					/>
                 </label>
                 <label className="label-add" htmlFor='id' >
                    DETAILS :
                    <input className="input-add"
					id='id'
                    onSubmit={handleSubmit}
					onChange={handleChange}
					type="text" 
					name="details" 
					value= {newShoe.details}
					/>
                 </label>
                 <label className="label-add" htmlFor='id'>
                    DESCRIPTION :
                    <input className="input-add"
					id='id'
                    onSubmit={handleSubmit}
					onChange={handleChange}
					type="text" 
					name="description" 
					value={newShoe.description}
					/>
                 </label>
                 <label className="label-add" htmlFor='id'>
                    BRAND NAME :
                    <input className="input-add"
					id='id'
                    onSubmit={handleSubmit}
					onChange={handleChange}
					type="text" 
					name="brand_name" 
					value={newShoe.brand_name}
					/>
                 </label>
                 <label className="label-add" htmlFor='id'>
                    BRAND URL :
                    <input className="input-add"
					id='id'
                    onSubmit={handleSubmit}
					onChange={handleChange}
					name="brand_url" 
					value={newShoe.brand_url}
					/>
                 </label>
                 <label className="label-add" htmlFor='id'>
                    SHOE URL :
                    <input className="input-add"
					id='id'
                    onSubmit={handleSubmit}
					onChange={handleChange}
					name="shoe_url" 
					value={newShoe.shoe_url}
					 />
                 </label>
				 <label className="label-add" htmlFor='id'>
                  PHOTO :
                    <input className="input-add"
					id='photo'
					onChange={e=>setNewShoe({...newShoe,photo: e.target.files[0]})}
					name="photo" 
					type="file"
					/>
                 </label>
                 <button className='app-button' type='submit' onclick={handleEdit}> Submit </button>
				 <button className='app-button' onclick={handleEdit}>Cancel</button>
            </form>
            </>
          )}
        </>
    );
};


export default Details;
